typedef struct{
	char ** title;		
	int size;
}State;

struct Input{
	char name[20];
	int type;     // 0 = Arg  ;  1 = Var
	int lvl;
	struct Input * prev;
	int pos;
};

typedef struct Input Input;
Input * build(Input *, char *);
void Free(Input *);
void print(Input );
Input * parse(Input *);
int evaluate(Input *, Input *, int);
