#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include "kardinal.h"

#define MAXBUF 100

void header(){
	printf("Debug : ");
}

Input * build(Input * In, char *in){
	// Declarations
	char buf[MAXBUF];
	int i = 0;
	int index = 0;
	int lvl = 0;
	int maxlvl = 0;
	char ch = in[i];
		
	// Remove preceding spaces
	while(ch == ' '){
		i++;
		ch = in[i];
	}
	printf("Debug : Removed spaces\n");	
	int flag = 0;
	while(i<strlen(in)){
		ch = in[i];
//		Input * cmd;
		if(ch == '('){
			buf[index] = '\0';
			if(flag==0){
				strcpy(In->name,buf);
				In->type = 1;
				In->prev = NULL;
				In->lvl = lvl;
			}else{
				Input * new = (Input *) malloc (sizeof(Input));
				strcpy(new->name,buf);
				new->type = 1;				
				new->prev = In;
				new->lvl = lvl;
				In = new;
			}
			flag ++;
			index = 0;
			lvl+=1;
		}else if(ch == ',' || ch == ')' && index!=0){
			buf[index] = '\0';
		//	printf("buf : %s\n",buf);
			Input * new = (Input *) malloc (sizeof(Input));
			strcpy(new->name,buf);
			new->type = 0;
			new->lvl = lvl;
			maxlvl = (lvl>maxlvl)?lvl:maxlvl;
			new->prev = In;
	//		new->parent = In;
			In = new;
			flag++;
			index = 0;
			if(ch == ')' && i != strlen(in)-1){
	// Skip the next 'comma' Ex : var(var2(arg1,arg2),arg3) Skip the comma after arg2) and go straight to arg3
				lvl -= 1;
				i++;
				ch = in[i];
				while(ch == ' ' || ch == ','){
					i++;
					ch = in[i];
				}
				continue;
			} 
		}else if(ch != ' ' && index < MAXBUF && ch!=')'){
			buf[index] = ch;
			index++;
		//	printf("buf : %s\n",buf);
		}
		i++;
	}
//	printf("%s : %d : %d : %s\n\n\n",In->name,In->lvl,In->type,In->prev->name);
	return In;	
}

void Free(Input * In){
	while(In->prev != NULL){
		Input * tmp = In;
		In = In->prev;
		free(tmp);
	}
	free(In);
}

int find_maxlvl(Input In){
	Input *tmp = &In;
	int maxlvl = 0;	
	while(tmp!=NULL){
		maxlvl = (tmp->lvl>maxlvl)?tmp->lvl:maxlvl;	
		tmp = tmp->prev;
	}
	return maxlvl;
}

void print(Input In){
	Input *tmp = &In;
//	printf("%d\n",strcmp(In.name,"def\0"));
	int flag = 0;
//	printf("i : str : t : l\n");
	while(tmp != NULL){
		printf("%d : %s : %d : %d\n",flag,tmp->name,tmp->type,tmp->lvl);
		tmp = tmp->prev;
		flag++;
	}
}

Input * sel_free(Input *In, int lvl_limit){
	Input *cur = In;
	Input *old = NULL;
	while(cur!=NULL){
		if(cur->lvl == lvl_limit){
			Input * tmp = cur;
			cur = cur->prev;
			if(old!=NULL)
				old->prev = cur;
			else
				In = cur;
			free(tmp);
		}else{
			Input *tmp = cur;
			cur = cur->prev;
			old = tmp;
		}
	}
	return In;
}

int write(Input *In1, Input *In2){
	strcpy(In1->name,In2->name);
	In1->type = In2->type;
	In1->lvl = In2->lvl;
	In1->prev = In2->prev;
}

void sequential_print(Input *In,int size){
	int i;
	for(i=0;i<size;i++){
		printf("Debug : In[%d].name = %s\n",i+1,In[i].name);
	}
}

Input * selective_free(Input *In, int lev_limit, int *size_args){
	int i,j;
	int size = *size_args;
	int flag=0;
//	sequential_print(In,size);
	for(i=0;i<size;i++){
		if(In[i].lvl==lev_limit){
			// Delete element
			for(j=i+1;j<size;j++){
				write(&In[j-1],&In[j]);
			}	
			flag++;
		}
	}
	printf("Debug : Flag : %d\nDebug : size_args : %d\n",flag,size);
	Input *tmp = (Input *) malloc ((size-flag)*sizeof(Input));
	for(i=0;i<size-flag;i++){
		write(&tmp[i],&In[i]);
	}
	
	free(In);
	*size_args = size-flag; 
	return tmp;
}

Input * evaluate(Input * In, Input * args, int size_args){
	strcpy(In->name,"Result");
	In->type = 0;
}

Input * parse(Input * In){
	int i,j;
	int size_argsold = 0;
	int maxlvl = find_maxlvl(*In);
//	printf("Debug : maxlvl = %d\n",maxlvl);	
	int curlvl = In->lvl;
	
	Input *argsold = (Input *) malloc (sizeof(Input));
	Input *args = (Input *) malloc (sizeof(Input));
	Input *old = In;
	Input *cur = In->prev;
	printf("Debug : Entered parse\n");

	while(cur!=NULL){
		if(cur->lvl > curlvl){
			printf("Debug : Entered if stmt 1\n");
			argsold = (Input *) realloc (argsold,(size_argsold+2)*sizeof(Input));
			printf("Debug : Survived realloc\n");
			size_argsold++;
			write(&argsold[size_argsold],old);
			sequential_print(argsold,size_argsold);
		//	old = cur;
			curlvl = cur->lvl;
		}else if(cur->lvl < curlvl){
			int size_args = 1;
			printf("Debug : Entered if stmt 2\n");
/*			for(j=0;j<size_argsold;j++){
				if(argsold[i].lvl == cur->lvl+1){
					args = (Input *) realloc (args,sizeof(Input)*(size_args+1));
					write(&args[size_args],&argsold[i]);
					size_args+=1;
				}
			}
*/
			printf("Debug : Survived the args loop\n");
			argsold = selective_free(argsold,cur->lvl+1,&size_argsold);
			sequential_print(argsold,size_argsold);
			printf("Debug : Survived the selective_free\n");
			cur = evaluate(cur,args,size_args-1);
			curlvl = cur->lvl;
		}else{
			printf("Debug : Entered if stmt 3\n");
			argsold = (Input *) realloc (argsold,(size_argsold+2)*sizeof(Input));
			printf("Debug : Survived Realloc if stmt 3\n");
			write(&argsold[size_argsold],cur);
			sequential_print(argsold,size_argsold);
			size_argsold+=1;
			curlvl = cur->lvl;
		}
//		sequential_print(argsold,size_argsold);
		old = cur;
		cur = cur->prev;	
	}
	printf("Debug : Survived the core loop!\n");
//	In = selective_free(In,maxlvl);
//	Input *old = 
//	Input *tmp = In;
//	Input *argsold;
//	Input *args = (Input *) malloc (sizeof(Input));	
//	int curlvl = In->lvl;
//	int cmd_exec = 0;
	free(args);
	return In;	
}
